import React from 'react'
import PokemonCard from './PokemonCard'
import './style.css'
import pokemonsData from './pokemons.json'

export default function PokemonList()
{
    const color = {poison : "purple", fire : "red"}
    return(
        <div className="list">
            {pokemonsData.map((item, id) => {
                return(
                    <div key={id}>
                        <PokemonCard id={item.id} name={item.names.fr} img={item.image} type={item.types.map(function (typ, i){return(<span key={i} style={{backgroundColor : colors[typ]}}>{typ} </span>)})}/>
                    </div>
                );
            })}
        </div>
    );
}
